CREATE VIEW `V_CloseReceiptControl` AS select `tbl`.`ReceiptSeq` AS `ReceiptSeq`,`tbl`.`ReceiptProcessDate` AS `ReceiptProcessDate`,`tbl`.`ReceiptDate` AS `ReceiptDate`,`tbl`.`ReceiptClass` AS `ReceiptClass`,`tbl`.`ReceiptAmount` AS `ReceiptAmount`,`tbl`.`ClaimId` AS `ClaimId`,`tbl`.`OrderSeq` AS `OrderSeq`,`tbl`.`CheckingUseAmount` AS `CheckingUseAmount`,`tbl`.`CheckingClaimFee` AS `CheckingClaimFee`,`tbl`.`CheckingDamageInterestAmount` AS `CheckingDamageInterestAmount`,`tbl`.`CheckingAdditionalClaimFee` AS `CheckingAdditionalClaimFee`,`tbl`.`DailySummaryFlg` AS `DailySummaryFlg`,`tbl`.`BranchBankId` AS `BranchBankId`,`tbl`.`DepositDate` AS `DepositDate`,`tbl`.`ReceiptAgentId` AS `ReceiptAgentId`,`tbl`.`RegistDate` AS `RegistDate`,`tbl`.`RegistId` AS `RegistId`,`tbl`.`UpdateDate` AS `UpdateDate`,`tbl`.`UpdateId` AS `UpdateId`,`tbl`.`ValidFlg` AS `ValidFlg` from `T_ReceiptControl` `tbl` where (`tbl`.`ReceiptSeq` = (select min(`rc`.`ReceiptSeq`) from (`T_ClaimControl` `cc` join `T_ReceiptControl` `rc`) where ((`cc`.`OrderSeq` = `rc`.`OrderSeq`) and (`cc`.`OrderSeq` = `tbl`.`OrderSeq`) and ((`cc`.`MinClaimAmount` - (select sum(`T_ReceiptControl`.`ReceiptAmount`) from `T_ReceiptControl` where ((`T_ReceiptControl`.`OrderSeq` = `cc`.`OrderSeq`) and (`T_ReceiptControl`.`ReceiptSeq` <= `rc`.`ReceiptSeq`)))) <= 0) and (`rc`.`ReceiptSeq` > ifnull((select max(`sub1`.`ReceiptSeq`) from (`T_ReceiptControl` `sub1` join `AT_ReceiptControl` `sub2`) where ((`sub1`.`ReceiptSeq` = `sub2`.`ReceiptSeq`) and (`sub1`.`OrderSeq` = `rc`.`OrderSeq`) and (`sub2`.`Rct_CancelFlg` = 1))),0)))));

