/* T_ImportedAccountTransferFile登録 */
DROP TABLE IF EXISTS `T_ImportedAccountTransferFile`;
CREATE TABLE `T_ImportedAccountTransferFile` (
  `Seq` bigint(20) NOT NULL AUTO_INCREMENT,
  `FileName` varchar(255) DEFAULT NULL,
  `Status` int(11) NOT NULL DEFAULT 0,
  `ReceiptResult` text,
  `RegistDate` datetime DEFAULT NULL,
  `RegistId` int(11) DEFAULT NULL,
  `UpdateDate` datetime DEFAULT NULL,
  `UpdateId` int(11) DEFAULT NULL,
  `ValidFlg` int(11) NOT NULL DEFAULT '1',
  PRIMARY KEY (`Seq`),
  UNIQUE KEY `AccountTransferFileName` (`FileName`),
  KEY `Idx_T_ImportedAccountTransferFile01` (`FileName`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/* T_ClaimAccountTransferFile登録 */
DROP TABLE IF EXISTS `T_ClaimAccountTransferFile`;
CREATE TABLE `T_ClaimAccountTransferFile` (
  `Seq` bigint(20) NOT NULL AUTO_INCREMENT,
  `FileName` varchar(255) DEFAULT NULL,
  `Status` int(11) NOT NULL DEFAULT 0,
  `ClaimFile` longblob,
  `RegistDate` datetime DEFAULT NULL,
  `RegistId` int(11) DEFAULT NULL,
  `UpdateDate` datetime DEFAULT NULL,
  `UpdateId` int(11) DEFAULT NULL,
  `ValidFlg` int(11) NOT NULL DEFAULT '1',
  PRIMARY KEY (`Seq`),
  UNIQUE KEY `ClaimAccountTransferFileName` (`FileName`),
  KEY `Idx_T_ClaimAccountTransferFile01` (`FileName`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

